import React, {Component} from 'react';
import { Row, Col, CardBody,CardText } from 'reactstrap';


class Cards extends Component {
  state = {
    try: [
      {
        title: 'git config',
        description: 'This command sets the author name and email address respectively to be used with your commits.',
        cli: 'git config'
      },
      {
        title: 'Second Command',
        description: 'With supporting text below as a natural lead-in to additional content.',
        cli: 'git-pull'
      }
    ]
  }

  constructor( props ){
    super( props )
    this.state = { show: true };

    this.toggleDiv = this.toggleDiv.bind(this)
  }

  toggleDiv = () => {
    const { show } = this.state;
    this.setState( { show: !show } )
  }

    render() {
      const firstSection = this.state.try.map((item, index) =>
        <Col className="card" sm="2" md="6" lg="4" key={index}>
            <CardBody>
                <p className="card-title">{`${item.title}`}</p>
                <CardText>{`${item.description}`}</CardText>
                <div className="descriptionShow">
                  <br />
                    <button onClick={ this.toggleDiv } className="fas fa-plus-circle fa-3x"></button>
                    <br /><br />
                    { this.state.show && <Box /> }
                </div>
                <a className="Clickbutton">{`${item.cli}`}</a>
            </CardBody>
        </Col>
      )
      return (
        <div className="container-fluid">
            <Row>
              {firstSection}
          </Row>
      </div>);
    }
  }

export default Cards;
