import React, { Component } from 'react';

class GitDescription {
  constructor( props ) {
    // super( props )
    this.state = { show: true };

    this.toggleDiv = this.toggleDiv.bind(this)
  }

  toggleDiv = () => {
    const { show } = this.state;
    this.setState( { show: !show } )
  }

  render() {
    return (
      <div className="descriptionShow">
        <br />
          <button onClick={ this.toggleDiv } className="fas fa-plus-circle fa-3x"></button>
          <br /><br />
          { this.state.show && <Box /> }
      </div>
    );
  }
}

class Box extends Component{
  render(){
    return(
      <div>This is div.</div>
    );
  }
}
export default GitDescription;
